parameters.jsCode:
// Calculate time since cart was last updated
const updatedAt = new Date($input.item.json.updated_at);
const now = new Date();
const hoursSinceUpdate = (now - updatedAt) / (1000 * 60 * 60);
const daysSinceUpdate = hoursSinceUpdate / 24;

const recoveryStage = $input.item.json.recovery_stage || 0;

// Determine next action based on recovery stage and time elapsed
let shouldSendEmail = false;
let emailStage = 0;
let stageName = '';
let discountCode = null;
let urgencyMessage = '';

// Stage 1: 1 hour after abandonment
if (recoveryStage === 0 && hoursSinceUpdate >= 1) {
  shouldSendEmail = true;
  emailStage = 1;
  stageName = 'reminder';
  urgencyMessage = 'Your cart is waiting for you!';
}

// Stage 2: 24 hours after abandonment
if (recoveryStage === 1 && hoursSinceUpdate >= 24) {
  shouldSendEmail = true;
  emailStage = 2;
  stageName = 'urgency';
  urgencyMessage = 'Still thinking? These items are popular and may sell out soon.';
}

// Stage 3: 72 hours (3 days) after abandonment
if (recoveryStage === 2 && hoursSinceUpdate >= 72) {
  shouldSendEmail = true;
  emailStage = 3;
  stageName = 'discount';
  discountCode = 'COMEBACK10';
  urgencyMessage = 'Special offer just for you! Get 10% off your cart.';
}

// Stage 4: 7 days (168 hours) after abandonment
if (recoveryStage === 3 && hoursSinceUpdate >= 168) {
  shouldSendEmail = true;
  emailStage = 4;
  stageName = 'final';
  urgencyMessage = 'Last chance! Check out these alternatives you might love.';
}

return {
  ...($input.item.json),
  _metadata: {
    hoursSinceUpdate,
    daysSinceUpdate,
    shouldSendEmail,
    emailStage,
    stageName,
    discountCode,
    urgencyMessage,
    currentRecoveryStage: recoveryStage
  }
};

// Merge exclusion check results
const cartData = $input.first().json;
const orderCheck = $input.all()[0].json;
const klaviyoCheck = $input.all()[1].json;
const zendeskCheck = $input.all()[2].json;

// Check exclusion conditions
const hasRecentOrder = orderCheck.orders && orderCheck.orders.length > 0;
const isSuppressed = klaviyoCheck.records && klaviyoCheck.records.length > 0;
const hasOpenTicket = zendeskCheck.results && zendeskCheck.results.length > 0;

const shouldExclude = hasRecentOrder || isSuppressed || hasOpenTicket;

return {
  ...cartData,
  _exclusionChecks: {
    hasRecentOrder,
    isSuppressed,
    hasOpenTicket,
    shouldExclude,
    exclusionReason: hasRecentOrder ? 'recent_order' : 
                     isSuppressed ? 'suppression_list' :
                     hasOpenTicket ? 'open_support_ticket' : null
  }
};

// Prepare email data with personalization
const cart = $input.first().json;
const algoliaResults = $input.last().json;

const emailStage = cart._metadata.emailStage;
const stageName = cart._metadata.stageName;

// Build email template data
const emailData = {
  customer_email: cart.customer_email,
  customer_first_name: cart.customer_first_name || 'Valued Customer',
  cart_id: cart.cart_id,
  customer_id: cart.customer_id,

  // Cart details
  cart_items: cart.items.map(item => ({
    name: item.name,
    sku: item.sku,
    image_url: item.image_url,
    price: item.price.toFixed(2),
    quantity: item.quantity,
    subtotal: (item.price * item.quantity).toFixed(2)
  })),

  cart_total: cart.total_value.toFixed(2),
  item_count: cart.item_count,

  // Stage-specific content
  email_stage: emailStage,
  stage_name: stageName,
  urgency_message: cart._metadata.urgencyMessage,
  discount_code: cart._metadata.discountCode,

  // Related products for stages 2 and 4
  related_products: algoliaResults.hits ? algoliaResults.hits.slice(0, 4).map(hit => ({
    name: hit.name,
    sku: hit.sku,
    image_url: hit.image_url,
    price: hit.price.toFixed(2),
    url: `https://broxiva.com/products/${hit.slug}`
  })) : [],

  // Email metadata
  cart_url: `https://broxiva.com/cart/${cart.cart_id}`,
  checkout_url: `https://broxiva.com/checkout/${cart.cart_id}`,

  // Tracking
  campaign_name: `abandoned_cart_stage_${emailStage}`,

  // Original cart data for updates
  _original: cart
};

return emailData;

