  },
  time: Math.floor(Date.now() / 1000)
}) }}

={{ JSON.stringify({
  recovery_stage: $json.email_stage,
  last_recovery_email_sent: new Date().toISOString(),
  recovery_campaign: $json.campaign_name
}) }}

={{ JSON.stringify([{
  event: 'Abandoned Cart Email Sent',
  properties: {
    token: $env.MIXPANEL_PROJECT_TOKEN,
    distinct_id: $json.customer_id,
    email: $json.customer_email,
    cart_id: $json.cart_id,
    email_stage: $json.email_stage,
    stage_name: $json.stage_name,
    cart_value: parseFloat($json.cart_total),
    item_count: $json.item_count,
    discount_offered: $json.discount_code || null,
    campaign: $json.campaign_name,
    time: Math.floor(Date.now() / 1000)
  }
}]) }}

={{ JSON.stringify([{
  event: 'Cart Excluded from Recovery',
  properties: {
    token: $env.MIXPANEL_PROJECT_TOKEN,
    distinct_id: $json.customer_id,
    email: $json.customer_email,
    cart_id: $json.cart_id,
    exclusion_reason: $json._exclusionChecks.exclusionReason,
    has_recent_order: $json._exclusionChecks.hasRecentOrder,
    is_suppressed: $json._exclusionChecks.isSuppressed,
    has_open_ticket: $json._exclusionChecks.hasOpenTicket,
    cart_value: $json.total_value,
    time: Math.floor(Date.now() / 1000)
  }
}]) }}

parameters.jsCode:
// Summary of workflow execution
const allItems = $input.all();

const summary = {
  execution_time: new Date().toISOString(),
  total_carts_processed: allItems.length,
  emails_sent: allItems.filter(item => item.json.email_stage).length,
  excluded_carts: allItems.filter(item => item.json._exclusionChecks?.shouldExclude).length,
  by_stage: {
    stage_1: allItems.filter(item => item.json.email_stage === 1).length,
    stage_2: allItems.filter(item => item.json.email_stage === 2).length,
    stage_3: allItems.filter(item => item.json.email_stage === 3).length,
    stage_4: allItems.filter(item => item.json.email_stage === 4).length
  },
  exclusion_reasons: {
    recent_order: allItems.filter(item => item.json._exclusionChecks?.exclusionReason === 'recent_order').length,
    suppression_list: allItems.filter(item => item.json._exclusionChecks?.exclusionReason === 'suppression_list').length,
    open_ticket: allItems.filter(item => item.json._exclusionChecks?.exclusionReason === 'open_support_ticket').length
  },
  total_cart_value: allItems.reduce((sum, item) => sum + (item.json.total_value || 0), 0).toFixed(2)
};

console.log('Abandoned Cart Recovery Summary:', JSON.stringify(summary, null, 2));

return summary;
